import './App.css';
import getUser from '../service/get-user'
import getStarred from '../service/get-starred'
import getRepos from '../service/get-repo'
import ModalStarred from '../components/modals/modalStarred'
import ModalRepo from '../components/modals/modalRepo'
import Search from '../components/search/search'
import React, { useEffect, useState } from 'react';

function App() {

  const [users, setUsers] = useState([])
  const [starred, setStarred] = useState([])
  const [repos, setRepos] = useState([])
  // const [userNameSTATE, setUserName] = useState('')

  useEffect(() => {
    getUser().then((response)=>{
      setUsers(response)
    })
    console.log('user')
  },[])

  const callApiStarred = (userName) =>{
    getStarred(userName).then((response) => {
      setStarred(response)
    })
  }

  const callApiRepos = (userName) =>{
    getRepos(userName).then((response) => {
      setRepos(response)
    })
  }
  return (
    <div>
    <div>
      <h3>Lista de usu√°rios </h3>
      <Search/>
    </div>

    {users && users.map((item, index) =>
    <div key={index}> 
      <span> {item.login}</span>
      <ModalStarred/>
      <ModalRepo/>
     </div>
    )}

    </div>
  )
}

export default App
