import './App.css';
import getUser from '../service/get-user'
import ModalStarred from '../components/modals/modalStarred'
import ModalRepo from '../components/modals/modalRepo'
import Search from '../components/search/search'
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router'
import styled from 'styled-components'

const TitleUser = styled.span`
font-size: 20px;
color: blue;
`
const TitleUserName = styled.span`
font-size: 20px;
color: gray;
margin-left: 20px;
`
const SearchDiv = styled.div`
width: 300px;
display: inline-block;
`
const ButtonDiv = styled.div`
margin-right: 30px;
display: inline-block;
`

const InitialDiv = styled.div`
display: flex;
/* flex-wrap: wrap;
align-items: center;
margin-bottom: 5px;
margin-top: 20px;
padding: 10px; */
`

const Initial = styled.div`
display: inline-block;
flex-wrap: wrap;
border-radius: 5px;
box-shadow: 0px 3px 6px #00000029;
justify-content: space-between;
align-items: center;
margin-bottom: 5px;
margin-top: 20px;
padding: 10px;
width: 250px;
`

const Image = styled.img`
border-radius: 50%;
width: 50px;
`

function App() {

  let {userNameUrl} = useParams()
  const [users, setUsers] = useState([])
  let [searchUserNameResult, setSearchUsernameResult] = useState(
		typeof window !== 'undefined' ? localStorage.getItem('searchUserNameResult') || '' : ''
	)

  useEffect(() => {

    if (userNameUrl){
      getUser(true, userNameUrl).then((response)=>{
        setUsers(new Array(response))
      })
    }else {
      getUser(false, '').then((response)=>{
        setUsers(response)
      })
    }
  },[])

  const handleChangeInput = event => setSearchUsernameResult(event)

  if (typeof window !== 'undefined') {
		localStorage.setItem('searchUserNameResult', searchUserNameResult)
	}

  let filteredResult =
  users &&
		users.filter(
			item =>
      item.login.toString().toLowerCase().includes(searchUserNameResult.toLowerCase())  
		)

  return (
    <>
    <InitialDiv>
      <TitleUser>Lista de usu√°rios </TitleUser>
      <SearchDiv>
        <Search handleChangeInput={handleChangeInput} searchUserNameResult={searchUserNameResult}/>
      </SearchDiv>
    </InitialDiv>

    {filteredResult && filteredResult.map((item, index) =>
      <Initial key={index}> 
      <Image src={item.avatar_url}/>
        <TitleUserName> {item.login}</TitleUserName>
        <ButtonDiv>
          <ModalStarred userName={item.login} />
          <ModalRepo userName={item.login} />
        </ButtonDiv>
     </Initial>
    )}

    </>
  )
}

export default App
