import baseUrl from './baseUrl'

const getUser = async (filterByUserName, userName) => {
    let url = ''
    if(filterByUserName) url = `/users/${userName}`
    else url = '/users?per_page=20'



    try{
        let response = await baseUrl.get(url,{

            headers:{
                Authorization:'token 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'
            }
        })
       return await response.data

    } catch(err) {
       return err.response

    }
}

export default getUser
