import './App.css';
import getUser from '../service/get-user'
import getStarred from '../service/get-starred'
import getRepos from '../service/get-repo'
import ModalStarred from '../components/modals/modalStarred'
import ModalRepo from '../components/modals/modalRepo'
import Search from '../components/search/search'
import React, { useEffect, useState } from 'react';

function App() {

  const [users, setUsers] = useState([])
  const [starred, setStarred] = useState([])
  const [repos, setRepos] = useState([])
  // const [userNameSTATE, setUserName] = useState('')
  let [searchUserNameResult, setSearchUsernameResult] = useState(
		typeof window !== 'undefined' ? localStorage.getItem('searchUserNameResult') || '' : ''
	)

  useEffect(() => {
    getUser().then((response)=>{
      setUsers(response)
    })
    console.log('user')
  },[])

  const callApiStarred = (userName) =>{
    getStarred(userName).then((response) => {
      setStarred(response)
    })
  }

  const callApiRepos = (userName) =>{
    getRepos(userName).then((response) => {
      setRepos(response)
    })
  }

  const handleChangeInput = event => setSearchUsernameResult(event)

  if (typeof window !== 'undefined') {
		localStorage.setItem('searchUserNameResult', searchUserNameResult)
	}
  const verifyUserNames = () => {
    if (users.length > 0) {
      return (
        users.filter(namesResult =>
          namesResult.login.toString().toLowerCase().includes(namesResult.toLowerCase())
        ).length > 0
      )
    } else {
      return true
    }
  }

  let filteredResult =
		users &&
		users.filter(
			namesResult =>
      namesResult.toString().toLowerCase().includes(searchUserNameResult.toString().toLowerCase()) && verifyUserNames(namesResult)
		)
  return (
    <div>
    <div>
      <h3>Lista de usu√°rios </h3>
      <Search
      handleChangeInput={handleChangeInput}
      searchUserNameResult={searchUserNameResult}
      />
    </div>

    {users && filteredResult.map((namesResult, index) =>
    <div key={index}> 
      <span> {namesResult.login}</span>
      <ModalStarred/>
      <ModalRepo/>
     </div>
    )}

    </div>
  )
}

export default App
